%KLEIN1	Construct a Klein bottle.
%	Klein bottle generated by revolving the 
%	figure-8 curve defined by XYKLEIN.

%       C. Henry Edwards, Univerity of Georgia. 6/20/93.
%
%       Expo'fied: dlc, 7/30/93
%	Copyright (c) 1984-94 by The MathWorks, Inc.

% Gallery initialization =================
if ~exist('GalleryGUIFlag'), figNumber=0; end;
% Create help.
infoStr= ...                                            
        ['                                               '  
         ' Klein bottle generated by revolving a figure-8'  
         ' curve.                                        '  
         '                                               '  
         ' Thanks to C. Henry Edwards,                   '  
         ' Dept. of Mathematics, University of Georgia.  '  
         '                                               '  
         '                                               '  
         ' File name: klein1.m                           '];
gallinit(figNumber,infoStr);

ab = [0 2*pi];
rtr = [2 0.5 1];
if isstudent
   pq = [30 30];
else
   pq = [40 40];
end
box = [-3 3 -3 3 -2 2];
vue = [55 60];

set(gcf,'color',[.7 .8 .9]);
tube('xyklein',ab,rtr,pq,box,vue);
shading interp
colormap(pink);
