%TORI4	Constuct 4 linked tori.
%  	Four-linked unknotted tori generated
%  	by revolving 4 off-center circles.

%	C. Henry Edwards, Dept. of Mathematics, U of Ga.
%
%	Expo'fied: dlc, 7/30/93
%	Copyright (c) 1984-94 by The MathWorks, Inc.

% Gallery initialization =================
if ~exist('GalleryGUIFlag'), figNumber=0; end;

infoStr= ...                                           
        ['                                             '
         ' Four-linked unknotted tori generated by     '  
         ' revolving four off-center circles.          '  
         '                                             '  
         ' Thanks to C. Henry Edwards,                 '  
         ' Dept. of Mathematics, University of Georgia.'  
         '                                             '  
         '                                             '  
         ' File name: tori4.m                          '];

gallinit(figNumber,infoStr);

 ab = [0 2*pi];
rtr = [6 1 1];
 pq = [10 50];
box = [-6.6 6.6 -6.6 6.6 -3 3];
vue = [200 70];

 tube('xylink1a',ab,rtr,pq,box,vue)
 colormap(jet);
 hold on

 tube('xylink1b',ab,rtr,pq,box,vue)
 tube('xylink1c',ab,rtr,pq,box,vue)
 tube('xylink1d',ab,rtr,pq,box,vue)

 set(gcf,'color',[.8 .8 .8]);
 hold off;

